import { Label } from "./Label";
import { color } from "./Util";


export class HotKey extends Label{

    getNormalImage():string;
    setNormalImage(img:string):void;

    getHotImage():string;
    setHotImage(img:string):void;

    getFocusedImage():string;
    setFocusedImage(img:string):void;

    getDisabledImage():string;
    setDisabledImage(img:string):void;

    getNativeBkColor():color;
    setNativeBkColor(col:color):void;

    getHotKey():{keycode:number,modifiers:number};
    setHotKey(keycode:number,modifiers:number):void;
}