import { Label } from "./Label";


export class Button extends Label{

    getNormalImage():string;
    setNormalImage(img:string):void;

    getHotImage():string;
    setHotImage(img:string):void;
    getPushedImage():string;
    setPushedImage(img:string):void;
    getFocusedImage():string;
    setFocusedImage(img:string):void;
    getDisabledImage():string;
    setDisabledImage(img:string):void;
    getHotForeImage():string;
    setHotForeImage(img:string):void;
    getStateCount():number;
    setStateCount(count:number):void;

    getStateImage():string;
    setStateImage(img:string):void;

    bindTabIndex(idx:number):void;
    bindTabLayoutName(name:string):void;
    bindTriggerTabSel(sel:number):void;
    removeBindTabIndex():void;
    getBindTabLayoutIndex():number;
    getBindTabLayoutName():string;

    setHotFont(font:number):void;
    getHotFont():number;

    setPushedFont(font:number):void;
    getPushedFont():number;
    setFocusedFont(font:number):void;
    getFocusedFont():number;

    setHotBkColor(color:number):void;
    getHotBkColor():number;
    setPushedBkColor(color:number):void;
    getPushedBkColor():number;
    setDisabledBkColor(color:number):void;
    getDisabledBkColor():number;

    setHotTextColor(color:number):void;
    getHotTextColor():number; 

    setPushedTextColor(color:number):void;
    getPushedTextColor():number; 

    setFocusedTextColor(color:number):void;
    getFocusedTextColor():number; 
}

