import { Rect,Size } from './Util';
import { Control } from './Control'

export class Container extends Control{
    getItemAt(idx:number):Control;
    getItemIndex(ctrl:Control):number;
    setItemIndex(ctrl:Control,idx:number):boolean;
    getCount():number;
    add(ctrl:Control):boolean;    
    addAt(ctrl:Control,idx:number):boolean;    
    remove(ctrl:Control):boolean;
    removeAt(idx:number):boolean;
    removeAll():void;
    getInset():Rect;
    setInset(rc:Rect):void;
    getChildPadding():number;
    setChildPadding(padding:number):void;

    //DT_CENTER
    getChildAlign():number;
    setChildAlign(align:number):void;
    getChildVAlign():number;
    setChildVAlign(align:number):void;

    isAutoDestroy():boolean;
    setAutoDestroy(auto:boolean):void;
    isDelayedDestroy():boolean;
    setDelayedDestroy(des:boolean):void;
    isMouseChildEnabled():boolean;
    setMouseChildEnabled(enable:boolean):void;
    findSelectable(idx:number,forword:boolean):number;
    getClientPos():Rect;
    setSubControlText(name:string,text:string):boolean;
    setSubControlFixedHeight(name:string,height:number):boolean;
    setSubControlFixedWidth(name:string,width:number):boolean;

    setSubControlUserData(name:string,data:string):boolean;
    getSubControlText(name:string):string;
    getSubControlFixedHeight(name:string):number;
    getSubControlFixedWidth(name:string):number;
    getSubControlUserData(name:string):string;
    findSubControl(name:string):Control;
    getScrollPos():Size;
    getScrollRange():Size;
    setScrollPos(pos:Size):void;
    setScrollStepSize(step:number):void;
    getScrollStepSize():number;
    lineUp():void;
    lineDown():void;
    pageUp():void;
    pageDown():void;
    homeUp():void;
    endDown():void;
    lineLeft():void;
    lineRight():void;
    pageLeft():void;
    pageRight():void;
    homeLeft():void;
    endRight():void;
    enableScrollBar(vertical:boolean,horizontal:boolean):void;
    getVerticalScrollBar():Control;
    getHorizontalScrollBar():Control;

}
