    getBkColor():number;
    setBkColor(color:number):void;
    getBkColor2():number;
    setBkColor2(color:number):void;
    getBkColor3():number;
    setBkColor3(color:number):void;

    getForeColor():number;
    setForeColor(color:number):void;

    getBkImage():string;
    setBkImage(img:string):void;
    getForeImage():string;
    setForeImage(img:string):void;

    getFocusBorderColor():number;
    setFocusBorderColor(color:number):void;

    isColorHSL():boolean;
    setColorHSL(hsl:boolean):void;

    getBorderRound():boolean;
    setBorderRound(enable:boolean):void;

    getBorderSize():number;
    setBorderSize(size:number):void;
    setBorderSize(size:Rect):void;

    getBorderColor():number;
    setBorderColor(color:number):void;
    

    getLeftBorderSize():number;
    SetLeftBorderSize(size:number):void;

    getTopBorderSize():number;
