import { Control } from "DuiLib";
import { Rect } from "src/Util";

export class Label extends Control{

    getTextStyle():number;
    setTextStyle(style:number):void;
    getTextColor():number;
    setTextColor(color:number):void;

    getDisabledTextColor():number;
    setDisabledTextColor(color:number):void;

    getFont():number;
    setFont(font:number):void;

    getTextPadding():Rect;
    setTextPadding(padding:Rect):void;

    isShowHtml():boolean;
    setShowHtml(show:boolean):void;

    getAutoCalcWidth():boolean;
    setAutoCalcWidth(auto:boolean):void;
    getAutoCalcHeight():boolean;
    setAutoCalcHeight(auto:boolean):void;  

}

