import { Button, color } from "DuiLib";


export class Option extends Button{

    getSelectedImage():string;
    setSelectedImage(img:string):void;
    getSelectedHotImage():string;
    setSelectedHotImage(img:string):void;
    getSelectedPushedImage():string;
    setSelectedPushedImage(img:string):void;
    getSelectedTextColor():color;
    setSelectedTextColor(c:color):void;
    getSelectBkColor():color;
    setSelectedBkColor(c:color):void;
    getSelectedForedImage():string;
    setSelectedForedImage(img:string):void;
    getSelectedStateCount():number;
    setSelectedStateCount(count:number):void;
    getSelectedStateImage():string;
    setSelectedStateImage(img:string):void;
    getSelectedFont():number;
    setSelectedFont(font:number):void;
    getGroup():string;
    setGroup(group:string):void;
    isSelected():boolean;
    selected(sel:boolean):void;
}

export class CheckBox extends Option{
    setCheck(check:boolean):void;
    getCheck():boolean;
}
