import { color,Point,Rect } from "./Util";
import { Container } from "./Container";

export class RichEdit extends Container{
    setMultiLine(b:boolean):void;
    isMultiLine():boolean;
    setWantTab(watTab:boolean):void;
    isWantTab():boolean;
    setWantReturn(watRet:boolean):void;
    isWantReturn():boolean;
    setWantCtrlReturn(watCtrlRtn:boolean):void;
    isWantCtrlReturn():boolean;
    setTransparent(b:boolean):void;
    isTransparent():boolean;
    setRich(rich:boolean):void;
    isRich():boolean;
    setReadOnly(readonly:boolean):void;
    isReadOnly():boolean;
    setWordWrap(wordWrap:boolean):void;
    isWordWrap():boolean;
    setFont(font:number):void;
    getFont():number;

    //WS_*
    setWinStyle(style:number):void;
    getWinStyle():number;
    setTextColor(c:color):void;
    getTextColor():color;
    setLimitText(chars:number):void;
    getLimitText():number;
    getTextLength():number;
    getText():string;
    setText(text:string):void;
    isModify():boolean;
    setModify(modify:boolean):void;
    getSel():{min:number,max:number};
    setSel(min:number,max:number):void;
    setSel(range:{min:number,max:number}):void;
    replaceSel(str:string):void;
    getSelText():string;
    setSelAll():void;
    setSelNone():void;
    getSelectionType():number;
    getZoom():{num:number,den:number};
    setZoom(num:number,den:number):void;
    setZoom(zoom:{num:number,den:number}):void;
    setZoomOff():void;
    getAutoURLDetect():boolean;
    setAutoURLDetect(det:boolean):void;
    getEventMask():number;
    setEventMask(mask:number):void;
    getTextRange(min:number,max:number):string;
    hideSelection(hide:boolean,changeStyle:boolean):void;
    scrollCaret():void;
    insertText(pos:number,text:string,canUndo:boolean):number;
    appendText(text:string,canUndo:boolean):number;

    canUndo():boolean;
    canRedo():boolean;
    canPaste():boolean;
    redo():boolean;
    undo():boolean;
    clear():void;
    copy():void;
    cut():void;
    paste():void;
    getLineCount():number;
    getLine(idx:number,maxLen:number):string;
    lineIndex(line:number):number;
    lineLength(line:number):number;
    lineScroll(line:number,chars:number):boolean;
    getCharPos(ch:number):Point;

    lineFromChar(ch:number):number;
    posFromChar(ch:number):Point;
    charFromPos(pos:Point):number;
    emptyUndoBuffer():void;
    setUndoLimit(limit:number):void;
    setAccumulateDBCMode(b:boolean):void;
    isAccumulateDBCMode():boolean;
    setTextPadding(padding:Rect):void;
    getTextPadding():Rect;
    setNormalImage(img:string):void;
    getNormalImage():string;
    setHotImage(img:string):void;
    getHotImage():string;
    setFocusedImage(img:string):void;
    getFocusedImage():string;
    setDisabledImage(img:string):void;
    getDisabledImage():string;
    setTipValue(tip:string):void;
    getTipValue():string;
    setTipValueColor(c:color):void;
    getTipValueColor():color;

    //DT_*
    setTipValueAlign(align:number):void;
    getTipValueAlign():number;
}

